parameters.functionCode:
// Execute multiple API health checks
const checks = [];
const startTime = Date.now();

try {
  // OpenAI Check
  const openaiResponse = await fetch('https://api.openai.com/v1/models', {
    headers: {
      'Authorization': `Bearer ${process.env.OPENAI_API_KEY}`
    },
    signal: AbortSignal.timeout(5000)
  });
  checks.push({
    service: 'OpenAI',
    status: openaiResponse.ok ? 'healthy' : 'degraded',
    statusCode: openaiResponse.status,
    responseTime: Date.now() - startTime
  });
} catch (error) {
  checks.push({
    service: 'OpenAI',
    status: 'down',
    error: error.message
  });
}

try {
  // Anthropic Check
  const anthropicResponse = await fetch('https://api.anthropic.com/v1/messages', {
    method: 'GET',
    headers: {
      'x-api-key': process.env.ANTHROPIC_API_KEY,
      'anthropic-version': '2023-06-01'
    },
    signal: AbortSignal.timeout(5000)
  });
  checks.push({
    service: 'Anthropic',
    status: anthropicResponse.ok || anthropicResponse.status === 405 ? 'healthy' : 'degraded',
    statusCode: anthropicResponse.status,
    responseTime: Date.now() - startTime
  });
} catch (error) {
  checks.push({
    service: 'Anthropic',
    status: 'down',
    error: error.message
  });
}

try {
  // Shotstack Check
  const shotstackResponse = await fetch('https://api.shotstack.io/v1/sources', {
    headers: {
      'x-api-key': process.env.SHOTSTACK_API_KEY
    },
    signal: AbortSignal.timeout(5000)
  });
  checks.push({
    service: 'Shotstack',
    status: shotstackResponse.ok ? 'healthy' : 'degraded',
    statusCode: shotstackResponse.status,
    responseTime: Date.now() - startTime
  });
} catch (error) {
  checks.push({
    service: 'Shotstack',
    status: 'down',
    error: error.message
  });
}

try {
  // Resend Check
  const resendResponse = await fetch('https://api.resend.com/domains', {
    headers: {
      'Authorization': `Bearer ${process.env.RESEND_API_KEY}`
    },
    signal: AbortSignal.timeout(5000)
  });
  checks.push({
    service: 'Resend',
    status: resendResponse.ok ? 'healthy' : 'degraded',
    statusCode: resendResponse.status,
    responseTime: Date.now() - startTime
  });
} catch (error) {
  checks.push({
    service: 'Resend',
    status: 'down',
    error: error.message
  });
}

return {
  json: {
    apiChecks: checks,
    timestamp: new Date().toISOString()
  }
};

// Generate comprehensive health report
const items = $input.all();

// Initialize report
const report = {
  timestamp: new Date().toISOString(),
  overallStatus: 'healthy',
  services: {},
  metrics: {},
  issues: [],
  alerts: []
};

// Process database check
const dbCheck = items.find(item => item.json.database);
if (dbCheck) {
  const responseTime = dbCheck.json.responseTime || 0;
  report.services.database = {
    status: responseTime < 1000 ? 'healthy' : responseTime < 3000 ? 'degraded' : 'down',
    responseTime: `${responseTime}ms`
  };

  if (responseTime >= 3000) {
    report.issues.push('Database response time is critical');
    report.overallStatus = 'down';
  } else if (responseTime >= 1000) {
    report.issues.push('Database response time is slow');
    if (report.overallStatus === 'healthy') report.overallStatus = 'degraded';
  }
}

// Process API checks
const apiChecks = items.find(item => item.json.apiChecks);
if (apiChecks && apiChecks.json.apiChecks) {
  apiChecks.json.apiChecks.forEach(check => {
    report.services[check.service.toLowerCase()] = {
      status: check.status,
      statusCode: check.statusCode,
      responseTime: check.responseTime ? `${check.responseTime}ms` : 'N/A',
      error: check.error || null
    };

    if (check.status === 'down') {
      report.issues.push(`${check.service} API is down: ${check.error}`);
      report.overallStatus = 'down';
    } else if (check.status === 'degraded') {
      report.issues.push(`${check.service} API is degraded`);
      if (report.overallStatus === 'healthy') report.overallStatus = 'degraded';
    }
  });
}

// Process queue check
const queueCheck = items.find(item => item.json.stuckJobs !== undefined);
if (queueCheck) {
  const stuckJobs = queueCheck.json.stuckJobs || 0;
  const failedJobs = queueCheck.json.failedJobs || 0;

  report.metrics.queue = {
    stuckJobs,
    failedJobs,
    status: stuckJobs === 0 && failedJobs < 10 ? 'healthy' : failedJobs < 50 ? 'degraded' : 'down'
  };

  if (stuckJobs > 0) {
    report.issues.push(`${stuckJobs} jobs stuck in processing state`);
    report.alerts.push({
      severity: 'high',
      message: `Queue has ${stuckJobs} stuck jobs`
    });
    if (report.overallStatus === 'healthy') report.overallStatus = 'degraded';
  }

  if (failedJobs >= 50) {
    report.issues.push(`${failedJobs} failed jobs detected`);
    report.overallStatus = 'down';
  } else if (failedJobs >= 10) {
    report.issues.push(`${failedJobs} failed jobs detected`);
    if (report.overallStatus === 'healthy') report.overallStatus = 'degraded';
  }
}

// Process storage check
const storageCheck = items.find(item => item.json.storageUsage !== undefined);
if (storageCheck) {
  const usagePercent = storageCheck.json.storageUsage || 0;

  report.metrics.storage = {
    usagePercent: `${usagePercent}%`,
    status: usagePercent < 80 ? 'healthy' : usagePercent < 90 ? 'degraded' : 'critical'
  };

  if (usagePercent >= 90) {
    report.issues.push(`Storage usage critical: ${usagePercent}%`);
    report.alerts.push({
      severity: 'critical',
      message: `Storage usage at ${usagePercent}%`
    });
    report.overallStatus = 'down';
  } else if (usagePercent >= 80) {
    report.issues.push(`Storage usage high: ${usagePercent}%`);
    report.alerts.push({
      severity: 'warning',
      message: `Storage usage at ${usagePercent}%`
    });
    if (report.overallStatus === 'healthy') report.overallStatus = 'degraded';
  }
}

// Process error check
const errorCheck = items.find(item => item.json.errorCount !== undefined);
if (errorCheck) {
  const errorCount = errorCheck.json.errorCount || 0;
  const errorsByType = errorCheck.json.errorsByType || {};

  report.metrics.errors = {
    lastHour: errorCount,
    byType: errorsByType,
    status: errorCount < 10 ? 'healthy' : errorCount < 50 ? 'degraded' : 'critical'
  };

  if (errorCount >= 50) {
    report.issues.push(`${errorCount} errors in last hour`);
    report.overallStatus = 'down';
  } else if (errorCount >= 10) {
    report.issues.push(`${errorCount} errors in last hour`);
    if (report.overallStatus === 'healthy') report.overallStatus = 'degraded';
  }
}

// Add summary
report.summary = {
  totalServices: Object.keys(report.services).length,
  healthyServices: Object.values(report.services).filter(s => s.status === 'healthy').length,
  degradedServices: Object.values(report.services).filter(s => s.status === 'degraded').length,
  downServices: Object.values(report.services).filter(s => s.status === 'down').length,
  totalIssues: report.issues.length,
  totalAlerts: report.alerts.length
};

return {
  json: report
};

